#!/bin/sh# Start MediaSoup Server in the backgroundnode server.js &# Wait a few seconds to ensure the server is readysleep 3# Start FFmpeg (ensure it auto-restarts if it crashes)while true; do  echo "Starting FFmpeg..." ffmpeg -rtsp_transport tcp -i "rtsp://test:Test2025@80.37.229.214:39887/Streaming/Channels/102?transportmode=unicast" \    -vf "scale=1280:720,format=yuv420p" \    -c:v libx264 -preset ultrafast -tune zerolatency -g 30 -r 30 \    -pix_fmt yuv420p -b:v 1500k -bufsize 1500k \    -payload_type 101 -ssrc 1234567 -pkt_size 1200 -f rtp rtp://127.0.0.1:5004  echo "FFmpeg crashed. Restarting in 5 seconds..."  sleep 5done